import Widget from "@/components/Widget/Widget";
import { SemipolarSpinner } from "epic-spinners";
export default {
  name: "Affilliate",
  components: {
    Widget: Widget,
    SemipolarSpinner: SemipolarSpinner
  },
  data: function data() {
    return {
      changeform: {},
      addform: {
        StoreId: "",
        FileName: "",
        LocalID: ""
      },
      counts: 0,
      errorMessage: null,
      GetSentSMSlist: [],
      isLoading: false,
      search: {
        fileName: "",
        storeId: "",
        date: "",
        serialInfo: "",
        localID: ""
      },
      filesnames: [],
      filesnameslist: [],
      file: "",
      stores: [],
      storeslist: [],
      allserials: [],
      pag: 1,
      index: 0,
      length: "",
      Next: false,
      Previous: "",
      selected: [],
      selectAll: false,
      listid: []
    };
  },
  mounted: function mounted() {
    // this.getGetSerials();
    this.getGetFileName();
    this.getGetStore();
  },
  methods: {
    changeselect1: function changeselect1() {
      if (this.selected.length == 0) {
        this.$swal("خطا!", "يرجى التحديد  ", "error");
        return;
      }

      this.$bvModal.show("sentserials2");
    },
    changeselect: function changeselect() {
      var _this = this;

      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من نقل الى المخزن",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  انقل!",
        CancelButtonText: "الغاء"
      }).then(function (result) {
        if (result.isConfirmed) {
          _this.isLoading = true;

          for (var i = 0; i < _this.selected.length; i++) {
            _this.listid.push({
              id: _this.selected[i]
            });
          }

          _this.axios.put("Admin/ChangeSerialStoreByIds?storeId=".concat(_this.changeform.StoreId), _this.listid).then(function (res) {
            if (res) {
              _this.$swal("تم!", "تم النقل بنحاج", "success");

              _this.getGetSerials();

              _this.selectAll = false;
              _this.selected = [];
              _this.listid = [];

              _this.$bvModal.hide("sentserials2");

              _this.isLoading = false;
            }
          }).catch(function () {
            this.isLoading = false;
            this.$swal("خطا!", "خطا في عملية النقل  ", "error");
          });
        }
      });
    },
    deletselect: function deletselect() {
      var _this2 = this;

      if (this.selected.length == 0) {
        this.$swal("خطا!", "يرجى التحديد  ", "error");
        return;
      }

      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من الحذف",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  امسح!",
        CancelButtonText: "الغاء"
      }).then(function (result) {
        if (result.isConfirmed) {
          _this2.isLoading = true;

          for (var i = 0; i < _this2.selected.length; i++) {
            _this2.listid.push({
              id: _this2.selected[i]
            });
          }

          _this2.axios.put("Admin/DeletOldSerialStoreByIds", _this2.listid).then(function (res) {
            if (res) {
              _this2.$swal("تم!", "تم الحذف بنحاج", "success");

              _this2.getGetSerials();

              _this2.selectAll = false;
              _this2.selected = [];
              _this2.listid = [];
              _this2.isLoading = false;
            }
          }).catch(function () {
            this.isLoading = false;
            this.$swal("خطا!", "خطا في عملية الحذف  ", "error");
          });
        }
      });
    },
    select: function select() {
      this.selected = [];

      if (!this.selectAll) {
        for (var i in this.allserials) {
          this.selected.push(this.allserials[i].id);
        }
      }
    },
    handleFileUpload: function handleFileUpload() {
      this.file = this.$refs.file.files[0];
    },
    addserialsnow: function addserialsnow() {
      var _this3 = this;

      if (this.addform.LocalID == 0 || this.addform.StoreId == "" || this.addform.FileName == "" || this.file == "") {
        this.$swal("انتباه!", "جميع الخانات ملطوبة", "info");
      }

      var formData = new FormData();
      formData.append("StoreId", this.addform.StoreId);
      formData.append("FileName", this.addform.FileName);
      formData.append("LocalID", this.addform.LocalID);
      formData.append("file", this.file);
      this.axios.post("Admin/AddXlsxOldSerailFile", formData, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      }).then(function (res) {
        if (res) {
          _this3.$swal("تم!", "  تم الاضافة بنحاج", "success");

          _this3.$bvModal.hide("addModel");

          _this3.isLoading = false;
          _this3.file = "";
          _this3.addform = {};
        }
      }).catch(function () {
        this.isLoading = false;
        this.alert = true;
      });
    },
    sentserialssnow: function sentserialssnow() {
      var _this4 = this;

      this.isLoading = true;
      this.axios.put("Admin/ChangeSerialStore?serialInfo=".concat(this.changeform.serialInfo, "&storeId=").concat(this.changeform.StoreId)).then(function (res) {
        if (res) {
          _this4.$swal("تم!", "  تم الاضافة بنحاج", "success");

          _this4.$bvModal.hide("sentserials");

          _this4.isLoading = false;

          _this4.getGetSerials();

          _this4.changeform = {};
        }
      }).catch(function () {
        this.isLoading = false;
        this.alert = true;
      });
    },
    addserials: function addserials() {
      this.$bvModal.show("addModel");
    },
    sentserials: function sentserials() {
      this.$bvModal.show("sentserials");
    },
    getGetSerials: function getGetSerials() {
      var _this5 = this;

      if (this.search.localID == "") {
        this.$swal("انتباه", "يرجى اختيار القسم", "info");
        return;
      }

      this.isLoading = true;
      this.axios.get("Admin/GetOldSerial?skip=0&take=100&fileName=".concat(this.search.fileName, "&storeId=").concat(this.search.storeId, "&date=").concat(this.search.date, "&serialInfo=").concat(this.search.serialInfo)).then(function (res) {
        _this5.allserials = res.data.data;
        _this5.counts = res.data.message;
        _this5.isLoading = false;
      }).catch(function (err) {
        if (err) {
          _this5.isLoading = false;
          _this5.alert = true;
        }
      });
    },
    getMore: function getMore() {
      var _this6 = this;

      this.isLoading = true; // if (!this.name && !this.type && this.items.length === 0) return false;

      var tempPage = this.pag + 100;
      this.axios.get("Admin/GetOldSerial?skip=".concat(tempPage - 1, "&take=100&fileName=").concat(this.search.fileName, "&storeId=").concat(this.search.storeId, "&date=").concat(this.search.date, "&serialInfo=").concat(this.search.serialInfo)).then(function (res) {
        _this6.Previous = false;
        _this6.allserials = res.data.data;
        _this6.isLoading = false;
        _this6.pag = tempPage;
        _this6.length = res.data.data.length;

        if (res.data.data.length <= 9) {
          _this6.Next = true;
        }

        if (res.data.data == "") {
          _this6.Next = true;
          _this6.isLoading = false;
        }
      }).catch(function (err) {
        if (err) {
          _this6.Next = true;
          _this6.isLoading = false;
        }
      });
    },
    getLess: function getLess() {
      var _this7 = this;

      this.isLoading = true;
      var tempPage = this.pag - 100;
      this.axios.get("Admin/GetOldSerial?skip=".concat(tempPage - 1, "&take=100&fileName=").concat(this.search.fileName, "&storeId=").concat(this.search.storeId, "&date=").concat(this.search.date, "&serialInfo=").concat(this.search.serialInfo)).then(function (res) {
        _this7.allserials = res.data.data;
        _this7.isLoading = false;
        _this7.pag = tempPage;
        _this7.Next = false; // console.log(tempPage);

        if (tempPage == 1) {
          _this7.Previous = true;
        }

        _this7.length = res.data.data.length;
      }).catch(function (err) {
        if (err) {
          _this7.isLoading = false;
          _this7.Previous = true;
        }
      });
    },
    getGetFileName: function getGetFileName() {
      var _this8 = this;

      this.isLoading = true;
      this.axios.get("Admin/GetOldFilesName").then(function (res) {
        _this8.filesnames.push({
          value: "",
          text: "اختر"
        });

        _this8.filesnameslist = res.data.data;

        for (var i = 0; i < _this8.filesnameslist.length; i++) {
          var option = [];

          for (var key in _this8.filesnameslist[i]) {
            if (key == "value") {
              option["value"] = _this8.filesnameslist[i][key];
            } else if (key == "fileName") {
              option["text"] = _this8.filesnameslist[i][key];
            }
          }

          _this8.filesnames.push(Object.assign({}, option));
        }

        _this8.isLoading = false;
      }).catch(function (err) {
        if (err) {
          _this8.$swal("Error", "Internet Erorr", "error");

          _this8.isLoading = false;
        }
      });
    },
    getGetStore: function getGetStore() {
      var _this9 = this;

      this.isLoading = true;
      this.axios.get("Admin/GetStores").then(function (res) {
        _this9.stores.push({
          value: "",
          text: "اختر"
        });

        _this9.storeslist = res.data.data;

        for (var i = 0; i < _this9.storeslist.length; i++) {
          var option = [];

          for (var key in _this9.storeslist[i]) {
            if (key == "id") {
              option["value"] = _this9.storeslist[i][key];
            } else if (key == "storeName") {
              option["text"] = _this9.storeslist[i][key];
            }
          }

          _this9.stores.push(Object.assign({}, option));
        }

        _this9.isLoading = false;
      }).catch(function (err) {
        if (err) {
          _this9.$swal("Error", "Internet Erorr", "error");

          _this9.isLoading = false;
        }
      });
    },
    Deleteseach: function Deleteseach() {
      var _this10 = this;

      if (this.search.localID == "") {
        this.$swal("انتباه", "يرجى اختيار القسم", "info");
        return;
      }

      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من الحذف",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  امسح!",
        CancelButtonText: "الغاء"
      }).then(function (result) {
        if (result.isConfirmed) {
          _this10.isLoading = true;

          _this10.axios.get("Admin/DeleteOldSearchSerial?fileName=".concat(_this10.search.fileName, "&storeId=").concat(_this10.search.storeId, "&date=").concat(_this10.search.date, "&serialInfo=").concat(_this10.search.serialInfo)).then(function (res) {
            if (res) {
              _this10.allserials = [];

              _this10.$swal("تم!", "تم المسح بنحاج", "success");

              _this10.isLoading = false;
            }
          }).catch(function (err) {
            if (err) {
              _this10.isLoading = false;
              _this10.alert = true;
            }
          });
        }
      }).catch(function () {
        this.isLoading = false;
        this.$swal("خطا!", "خطا في عملية الحذف  ", "error");
      });
    }
  }
};
