// The Vue build version to load with the `import` command
// (runtime-only or standalone) has been set in webpack.base.conf with an alias.
import Vue from 'vue';
import BootstrapVue from 'bootstrap-vue';
import * as VueGoogleMaps from 'vue2-google-maps';
import VueTouch from 'vue-touch';
import Trend from 'vuetrend';
import Toasted from 'vue-toasted';
import VueApexCharts from 'vue-apexcharts';
import axios from 'axios';
import VueAxios from 'vue-axios';
import store from './store';
import router from './Routes';
import App from './App';
import layoutMixin from './mixins/layout';
import Widget from './components/Widget/Widget';
import OtpInput from "@bachdgvn/vue-otp-input";
import VueSwal from 'vue-swal'
Vue.component("v-otp-input", OtpInput);
Vue.use(VueSwal)
Vue.use(BootstrapVue);
Vue.use(VueTouch);
Vue.use(Trend);
Vue.component('Widget', Widget);
Vue.use(VueGoogleMaps, {
  load: {
    key: 'AIzaSyB7OXmzfQYua_1LEhRdqsoYzyJOPh9hGLg',
  },
});
Vue.component('apexchart', VueApexCharts);
Vue.mixin(layoutMixin);
Vue.use(Toasted, {duration: 10000});
Vue.use(VueAxios, axios);
// seeting baseUrl in axios 
axios.interceptors.request.use(
  (config) => {
    let token = localStorage.getItem('token');
    config.baseURL = "http://localhost:5000/api/";
    config.headers['Authorization'] = `Bearer ${ token }`;

    return config;
  },

  (error) => {
    return Promise.reject(error);
  }
);
const rolePage = {

  Admin: "/Admin/DashBoard",
  User: "/User/AddAffiliate",


};
router.beforeEach((to, form, next) => {
  const role = localStorage.getItem("token") && localStorage.getItem("role") ? localStorage.getItem("role") : null;
  if (to.path === "/login" && role) return next(); 
  if (to.path === "/login" && !role) return next();
  if (to.path === "/signUp" && role) return next(); 
  if (to.path === "/signUp" && !role) return next();
  if (to.path === "/login" && !role || to.path === "/login" && role) {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    return next();
  }
  if (to.path === "/logout") {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    next("/login");
  }

  if (to.meta.role) {
    if (to.meta.role === role) {
      next();
    } else {
      next("/login");
    }
  } else {
    if (role) return next(rolePage[role]);
    next("/");
  }
});

Vue.config.productionTip = false;

/* eslint-disable no-new */
new Vue({
  el: '#app',
  store,
  router,
  render: h => h(App),
});
