import Widget from "@/components/Widget/Widget";
import { SemipolarSpinner } from "epic-spinners";
export default {
  name: "Affilliate",
  components: { Widget, SemipolarSpinner },
  data() {
    return {
      changeform: {},
      addform: {
        StoreId: "",
        FileName: "",
        LocalID: "",
      },

      counts: 0,
      errorMessage: null,
      GetSentSMSlist: [],
      isLoading: false,
      search: {
        fileName: "",
        storeId: "",
        date: "",
        serialInfo: "",
        localID: "",
        cliantName : ""
      },
      filesnames: [],
      filesnameslist: [],
      file: "",
      stores: [],
      storeslist: [],
      allserials: [],
      pag: 1,
      index: 0,
      length: "",
      Next: false,
      Previous: "",
      selected: [],
      selectAll: false,
      listid: [],
      clinetlist :[],
      clinet :[],
      changeformtoclinet : {
        clinetid : "",
      },
      cartoonIdforlist : "",
      cartoonIdlist : [],
    };
  },
  mounted() {
    // this.getGetSerials();
    this.getGetFileName();
    this.getGetStore();
    this.getGetClinet();
  },
  methods: {
    addidtocartoonlist(){

      var ids = {
        "cartoonId" : this.cartoonIdforlist
      }
      this.cartoonIdlist.push(ids);

    },
        getGetClinet() {
      this.isLoading = true;
      this.axios
        .get("Admin/GetClient")
        .then((res) => {
          this.clinet.push({ value: "", text: "اختر" });
          this.clinetlist = res.data.data;
          for (var i = 0; i < this.clinetlist.length; i++) {
            var option = [];
            for (var key in this.clinetlist[i]) {
              if (key == "id") {
                option["value"] = this.clinetlist[i][key];
              } else if (key == "userPhone") {
                option["text"] = this.clinetlist[i][key];
              }
            }
            this.clinet.push(Object.assign({}, option));
          }

          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.$swal("Error", "Internet Erorr", "error");
            this.isLoading = false;
          }
        });
    },

    changeselect1() {
      if (this.selected.length == 0) {
        this.$swal("خطا!", "يرجى التحديد  ", "error");
        return;
      }
      this.$bvModal.show("sentserials2");
    },


    changeselect2() {
      if (this.selected.length == 0) {
        this.$swal("خطا!", "يرجى التحديد  ", "error");
        return;
      }
      this.$bvModal.show("sentserials3");
    },


  ChangeSerialStoreByCartoonId() {
      this.$bvModal.show("ChangeSerialStoreByCartoonId");
    },


       sentclinet() {
      this.$bvModal.show("sentclinet");
    },
        sentserialtoclinetssnow() {
      this.isLoading = true;
      this.axios
        .put(
          `Admin/ChangeSerialToClient?serialInfo=${this.changeform.serialInfo}&clientId=${this.changeformtoclinet.clinetid}`
        )
        .then((res) => {
          if (res) {
            this.$swal("تم!", "  تم الاضافة بنحاج", "success");
            this.$bvModal.hide("sentclinet");
            this.isLoading = false;
            this.getGetSerials();
            this.changeform = {};
          }
        })

        .catch(function() {
          this.isLoading = false;
          this.alert = true;
        });
    },

    changeselecttosalesepoint() {
      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من نقل الى نقطة البيع",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  انقل!",
        CancelButtonText: "الغاء",
      }).then((result) => {
        if (result.isConfirmed) {
          this.isLoading = true;
          for (var i = 0; i < this.selected.length; i++) {
            this.listid.push({ id: this.selected[i] });
          }
          this.axios
            .put(
              `Admin/ChangeToClientByIds?clientId=${this.changeformtoclinet.clinetid}`,
              this.listid
            )
            .then((res) => {
              if (res) {
                this.$swal("تم!", "تم النقل بنحاج", "success");
                this.getGetSerials();
                this.selectAll = false;
                this.selected = [];
                this.listid = [];
                this.$bvModal.hide("sentserials3");
                this.isLoading = false;
              }
            })

            .catch(function() {
              this.isLoading = false;
              this.$swal("خطا!", "خطا في عملية النقل  ", "error");
            });
        }
      });
    },
    restore(id) {
      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من استرجاع المباع",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  استرجع!",
        CancelButtonText: "الغاء",
      }).then((result) => {
        if (result.isConfirmed) {
          this.isLoading = true;
          this.axios
            .put(
              `Admin/RestoreSolidSeriel?serialId=${id}`,
              this.listid
            )
            .then((res) => {
              if (res) {
                this.$swal("تم!", "تم الاسترجاع بنحاج", "success");
                this.getGetSerials();
                this.selectAll = false;
                this.selected = [];
                this.listid = [];
         //       this.$bvModal.hide("sentserials3");
                this.isLoading = false;
              }
            })

            .catch(function() {
              this.isLoading = false;
              this.$swal("خطا!", "خطا في عملية النقل  ", "error");
            });
        }
      });
    },

    changeselect() {
      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من نقل الى المخزن",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  انقل!",
        CancelButtonText: "الغاء",
      }).then((result) => {
        if (result.isConfirmed) {
          this.isLoading = true;
          for (var i = 0; i < this.selected.length; i++) {
            this.listid.push({ id: this.selected[i] });
          }
          this.axios
            .put(
              `Admin/ChangeSerialStoreByIds?storeId=${this.changeform.StoreId}`,
              this.listid
            )
            .then((res) => {
              if (res) {
                this.$swal("تم!", "تم النقل بنحاج", "success");
                this.getGetSerials();
                this.selectAll = false;
                this.selected = [];
                this.listid = [];
                this.$bvModal.hide("sentserials2");
                this.isLoading = false;
              }
            })

            .catch(function() {
              this.isLoading = false;
              this.$swal("خطا!", "خطا في عملية النقل  ", "error");
            });
        }
      });
    },
    deletselect() {
      if (this.selected.length == 0) {
        this.$swal("خطا!", "يرجى التحديد  ", "error");
        return;
      }
      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من الحذف",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  امسح!",
        CancelButtonText: "الغاء",
      }).then((result) => {
        if (result.isConfirmed) {
          this.isLoading = true;
          for (var i = 0; i < this.selected.length; i++) {
            this.listid.push({ id: this.selected[i] });
          }
          this.axios
            .put("Admin/DeletSerialStoreByIds", this.listid)
            .then((res) => {
              if (res) {
                this.$swal("تم!", "تم الحذف بنحاج", "success");
                this.getGetSerials();
                this.selectAll = false;
                this.selected = [];
                this.listid = [];
                this.isLoading = false;
              }
            })

            .catch(function() {
              this.isLoading = false;
              this.$swal("خطا!", "خطا في عملية الحذف  ", "error");
            });
        }
      });
    },
    select() {
      this.selected = [];
      if (!this.selectAll) {
        for (let i in this.allserials) {
          this.selected.push(this.allserials[i].id);
        }
      }
    },
    handleFileUpload() {
      this.file = this.$refs.file.files[0];
    },

    addserialsnow() {
      if (
        this.addform.LocalID == 0 ||
        this.addform.StoreId == "" ||
        this.addform.FileName == "" ||
        this.file == ""
      ) {
        this.$swal("انتباه!", "جميع الخانات ملطوبة", "info");
      }
      let formData = new FormData();
      formData.append("StoreId", this.addform.StoreId);
      formData.append("FileName", this.addform.FileName);
      formData.append("LocalID", this.addform.LocalID);
      formData.append("file", this.file);
      this.axios
        .post("Admin/AddXlsxSerailFile", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        })
        .then((res) => {
          if (res) {
            this.$swal("تم!", "  تم الاضافة بنحاج", "success");
            this.$bvModal.hide("addModel");
            this.isLoading = false;
            this.file = "";
            this.addform = {};
          }
        })

        .catch(function() {
          this.isLoading = false;
          this.alert = true;
        });
    },
    sentserialssnow() {
      this.isLoading = true;
      this.axios
        .put(
          `Admin/ChangeSerialStore?serialInfo=${this.changeform.serialInfo}&storeId=${this.changeform.StoreId}`
        )
        .then((res) => {
          if (res) {
            this.$swal("تم!", "  تم الاضافة بنحاج", "success");
            this.$bvModal.hide("sentserials");
            this.isLoading = false;
            this.getGetSerials();
            this.changeform = {};
          }
        })

        .catch(function() {
          this.isLoading = false;
          this.alert = true;
        });
    },
    changecartoonidlistnow() {
      if(this.cartoonIdlist.length == 0){

        this.$swal("انتاه!", "يرجى اضافة رقم كارتو واحد كحد ادنى", "info");
      }
      this.isLoading = true;
      this.axios
        .put(
          `Admin/ChangeSerialStoreByCartoonId?storeId=${this.changeform.StoreId}`, this.cartoonIdlist
        )
        .then((res) => {
          if (res) {
            this.$swal("تم!", "  تم الاضافة بنحاج", "success");
            this.$bvModal.hide("ChangeSerialStoreByCartoonId");
            this.isLoading = false;
            this.getGetSerials();
            this.changeform = {};
          }
        })

        .catch(function() {
          this.isLoading = false;
          this.alert = true;
        });
    },
    addserials() {
      this.$bvModal.show("addModel");
    },
    sentserials() {
      this.$bvModal.show("sentserials");
    },

    getGetSerials() {
      if (this.search.localID == "") {
        this.$swal("انتباه", "يرجى اختيار القسم", "info");

        return;
      }
      this.isLoading = true;
      this.axios
        .get(
          `Admin/GetSerial?skip=0&take=100&fileName=${this.search.fileName}&storeId=${this.search.storeId}&date=${this.search.date}&serialInfo=${this.search.serialInfo}&cliantName=${this.search.cliantName}&localID=${this.search.localID}`
        )
        .then((res) => {
          this.allserials = res.data.data;
          this.counts = res.data.message;

          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.isLoading = false;
            this.alert = true;
          }
        });
    },

    getMore: function() {
      this.isLoading = true;

      // if (!this.name && !this.type && this.items.length === 0) return false;
      const tempPage = this.pag + 100;

      this.axios
        .get(
          `Admin/GetSerial?skip=${tempPage - 1}&take=100&fileName=${
            this.search.fileName
          }&storeId=${this.search.storeId}&date=${
            this.search.date
          }&serialInfo=${this.search.serialInfo}&cliantName=${this.search.cliantName}&localID=${this.search.localID}`
        )

        .then((res) => {
          this.Previous = false;
          this.allserials = res.data.data;
          this.isLoading = false;
          this.pag = tempPage;
          this.length = res.data.data.length;

          if (res.data.data.length <= 9) {
            this.Next = true;
          }
          if (res.data.data == "") {
            this.Next = true;
            this.isLoading = false;
          }
        })
        .catch((err) => {
          if (err) {
            this.Next = true;
            this.isLoading = false;
          }
        });
    },
    getLess: function() {
      this.isLoading = true;

      const tempPage = this.pag - 100;
      this.axios
        .get(
          `Admin/GetSerial?skip=${tempPage - 1}&take=100&fileName=${
            this.search.fileName
          }&storeId=${this.search.storeId}&date=${
            this.search.date
          }&serialInfo=${this.search.serialInfo}&cliantName=${this.search.cliantName}&localID=${this.search.localID}`
        )
        .then((res) => {
          this.allserials = res.data.data;
          this.isLoading = false;
          this.pag = tempPage;
          this.Next = false;
          // console.log(tempPage);
          if (tempPage == 1) {
            this.Previous = true;
          }
          this.length = res.data.data.length;
        })
        .catch((err) => {
          if (err) {
            this.isLoading = false;
            this.Previous = true;
          }
        });
    },

    getGetFileName() {
      this.isLoading = true;
      this.axios
        .get("Admin/GetFilesName")
        .then((res) => {
          this.filesnames.push({ value: "", text: "اختر" });
          this.filesnameslist = res.data.data;
          for (var i = 0; i < this.filesnameslist.length; i++) {
            var option = [];
            for (var key in this.filesnameslist[i]) {
              if (key == "value") {
                option["value"] = this.filesnameslist[i][key];
              } else if (key == "fileName") {
                option["text"] = this.filesnameslist[i][key];
              }
            }
            this.filesnames.push(Object.assign({}, option));
          }

          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.$swal("Error", "Internet Erorr", "error");
            this.isLoading = false;
          }
        });
    },
    getGetStore() {
      this.isLoading = true;
      this.axios
        .get("Admin/GetStores")
        .then((res) => {
          this.stores.push({ value: "", text: "اختر" });
          this.storeslist = res.data.data;
          for (var i = 0; i < this.storeslist.length; i++) {
            var option = [];
            for (var key in this.storeslist[i]) {
              if (key == "id") {
                option["value"] = this.storeslist[i][key];
              } else if (key == "storeName") {
                option["text"] = this.storeslist[i][key];
              }
            }
            this.stores.push(Object.assign({}, option));
          }

          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.$swal("Error", "Internet Erorr", "error");
            this.isLoading = false;
          }
        });
    },

    Deleteseach() {
      if (this.search.localID == "") {
        this.$swal("انتباه", "يرجى اختيار القسم", "info");

        return;
      }

      this.$swal({
        title: "هل انت متأكد?",
        text: "هل انت متأكد من الحذف",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "نعم ,  امسح!",
        CancelButtonText: "الغاء",
      })
        .then((result) => {
          if (result.isConfirmed) {
            this.isLoading = true;
            this.axios
              .get(
                `Admin/DeleteSearchSerial?fileName=${this.search.fileName}&storeId=${this.search.storeId}&date=${this.search.date}&serialInfo=${this.search.serialInfo}`
              )
              .then((res) => {
                if (res) {
                  this.allserials = [];
                  this.$swal("تم!", "تم المسح بنحاج", "success");
                  this.isLoading = false;
                }
              })
              .catch((err) => {
                if (err) {
                  this.isLoading = false;
                  this.alert = true;
                }
              });
          }
        })
        .catch(function() {
          this.isLoading = false;
          this.$swal("خطا!", "خطا في عملية الحذف  ", "error");
        });
    },
  },
};
