import Widget from "@/components/Widget/Widget";
import { SemipolarSpinner } from "epic-spinners";
export default {
  name: "Affilliate",
  components: { Widget, SemipolarSpinner },
  data() {
    return {
      errorMessage: null,
      GetSentSMSlist: [],
      isLoading: false,
      search: {
        fileName: "",
        storeId: "",
        date: "",
        serialInfo: "",
      },
      filesnames: [],
      filesnameslist: [],

      stores: [],
      storeslist: [],
      allserials: [],
      pag: 1,
      index: 0,
      length: "",
      Next: false,
      Previous: ""
    };
  },
  mounted() {
    this.getGetSerials();
    this.getGetFileName();
    this.getGetStore();
  },
  methods: {
    getGetSerials() {
      this.isLoading = true;
      this.axios
        .get(
          `Admin/GetSerial?skip=0&take=20&fileName=${this.search.fileName}&storeId=${this.search.storeId}&date=${this.search.date}&serialInfo=${this.search.serialInfo}`
        )
        .then((res) => {
          this.allserials = res.data.data;
          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.isLoading = false;
            this.alert = true;
          }
        });
    },

    getMore: function() {
      this.isLoading = true;

      // if (!this.name && !this.type && this.items.length === 0) return false;
      const tempPage = this.pag + 10;

      this.axios
        .get(
          `Admin/GetSerial?skip=${tempPage - 1}&take=20&fileName=${
            this.search.fileName
          }&storeId=${this.search.storeId}&date=${
            this.search.date
          }&serialInfo=${this.search.serialInfo}`
        )

        .then((res) => {
          this.Previous = false;
          this.allserials = res.data.data;
          this.isLoading = false;
          this.pag = tempPage;
          this.length = res.data.data.length;

          if (res.data.data.length <= 9) {
            this.Next = true;
          }
          if (res.data.data == "") {
            this.Next = true;
            this.isLoading = false;
          }
        })
        .catch((err) => {
          if (err) {
            this.Next = true;
            this.isLoading = false;
          }
        });
    },
    getLess: function() {
      this.isLoading = true;

      const tempPage = this.pag - 10;
      this.axios
        .get(
          `Admin/GetSerial?skip=${tempPage - 1}&take=20&fileName=${
            this.search.fileName
          }&storeId=${this.search.storeId}&date=${
            this.search.date
          }&serialInfo=${this.search.serialInfo}`
        )
        .then((res) => {
          this.allserials = res.data.data;
          this.isLoading = false;
          this.pag = tempPage;
          this.Next = false;
          // console.log(tempPage);
          if (tempPage == 1) {
            this.Previous = true;
          }
          this.length = res.data.data.length;
        })
        .catch((err) => {
          if (err) {
            this.isLoading = false;
            this.Previous = true;
          }
        });
    },

    getGetFileName() {
      this.isLoading = true;
      this.axios
        .get("Admin/GetFilesName")
        .then((res) => {
          this.filesnameslist = res.data.data;
          for (var i = 0; i < this.filesnameslist.length; i++) {
            var option = [];
            for (var key in this.filesnameslist[i]) {
              if (key == "value") {
                option["value"] = this.filesnameslist[i][key];
              } else if (key == "fileName") {
                option["text"] = this.filesnameslist[i][key];
              }
            }
            this.filesnames.push(Object.assign({}, option));
          }

          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.$swal("Error", "Internet Erorr", "error");
            this.isLoading = false;
          }
        });
    },
    getGetStore() {
      this.isLoading = true;
      this.axios
        .get("Admin/GetStores")
        .then((res) => {
          this.storeslist = res.data.data;
          for (var i = 0; i < this.storeslist.length; i++) {
            var option = [];
            for (var key in this.storeslist[i]) {
              if (key == "id") {
                option["value"] = this.storeslist[i][key];
              } else if (key == "storeName") {
                option["text"] = this.storeslist[i][key];
              }
            }
            this.stores.push(Object.assign({}, option));
          }

          this.isLoading = false;
        })
        .catch((err) => {
          if (err) {
            this.$swal("Error", "Internet Erorr", "error");
            this.isLoading = false;
          }
        });
    },
  },
};
